import { Redirect } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

const PostGame = ({ token, win, userKills }) => {

    const [ userData, setUserData ] = useState({ username: '', 
                                                total_games: 0,
                                                total_kills: 0,
                                                total_wins: 0 
                                            });

    let gameResults = 0
    const game = 1
    let newTotalGames = game + userData.total_games;

    if (win === true) {
        gameResults = 1
    }


   useEffect(() => {
    axios.post('http://localhost:5000/accounts/me', {
        token: token,
    })
    .then(response => {
        setUserData(response.data);
    });
}, [])

    if (!token) {
        return <Redirect to='/' />
    }

    return (
        <div className='postgame'>
            { !userData? <p> Loading data... </p> 
            : 
            <>
                {
                    win?
                        <h3>
                            Congratulations {userData.username} you won!
                        </h3>
                    :
                        <h3>
                            {userData.username}  you got caught!
                        </h3>
                }

                <h3>
                    You eliminated {userKills} players! 
                </h3>

                <p>
                    -----------------------------------------------------------------------------------------
                </p>

                <div className='endgame'>
                <h3>
                    Total Games: {userData.total_games || 0 } + {game} = {newTotalGames || 0}
                </h3>
                <h3>
                    Total Wins: {userData.total_wins || 0 } + {gameResults}
                </h3>
                <h3>
                    Total Kills: {userData.total_kills || 0 } + {userKills}
                </h3>
                <p>
                    &emsp;
                </p>
                <p>
                    Your account has been updated..
                </p>
                </div>
        </>
        }
        </div>
    );
}

export default PostGame;
