import { BrowserRouter as Router, Route, Switch, } from 'react-router-dom';
import { useState } from 'react';
import Home from './components/Home';
import SignUp from './components/SignUp';
import SignIn from './components/SignIn';
import ListAccounts from './components/ListAccounts';
import NavBar from './components/NavBar';
import AccountDetails from './components/AccountDetails';
import AccountShow from './components/AccountShow';
import Play from './components/Play';
import PostGame from './components/PostGame';

function App() {
  const [token, setToken] = useState();
  const [ inGame, setInGame ] = useState(false);


  return (
    <Router>
      <NavBar token={token} setToken={setToken} inGame={inGame}/>
      <div className='content' />
      <Switch>
        <Route exact path="/">
          <Home token={token} />
        </Route>
        <Route path="/signup">
          <SignUp />
        </Route>
        <Route path="/postgame">
          <PostGame token={token} />
        </Route>
        <Route path="/signin" >
          <SignIn setToken={setToken} token={token}/>
        </Route>
        <Route path="/accounts/:id">
          <AccountShow token={token}/>
        </Route>
        <Route path="/listaccounts">
          <ListAccounts  token={token}/>
        </Route>
        <Route path="/accountdetails">
          <AccountDetails token={token}/>
        </Route>
        <Route path="/play">
          <Play token={token} setToken={setToken} inGame={inGame} setInGame={setInGame} />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
