import React, {useState, useEffect} from 'react';

const SignUp = () => {

    const [values, setValues] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: ''
    });

    const [errors, setErrors] = useState({})

    const handleChanges = e => {
        setValues({
            ...values,
            [e.target.name]: e.target.value
        })
    }

    return(
        <fragment>
            <form className="sign Up">
                <h1 className="text-center mt-5"> Sign up!</h1>
                <h1>Get started with us today! Create your account by filling out the information below!</h1>
                <div>
                    <label for='username'>Username</label>
                    <input type='text' name='username' id='username' placeholder='Enter your username'></input>
                </div>
                <div>
                    <label for='email'>Email</label>
                    <input type='text' name='email' id='email' placeholder='Enter your email'></input>
                </div>
                <div>
                    <label for='password'>Password</label>
                    <input type='text' name='password' id='password' placeholder='Enter your password'></input>
                </div>
                <div>
                    <label for='confirmPassword'>Confirm Password</label>
                    <input type='text' name='confirmpassword' id='confirmPassword' placeholder='Confirm your password'></input>
                </div>
                <button className="signup-button" type="submit">Sign Up</button>
            </form>
        </fragment>
    )
}

export default SignUp;
