import { Redirect } from "react-router";
import { io } from 'socket.io-client'
import { useState, useEffect } from "react";
import axios from "axios";
import queryString from 'query-string';

const Multiplayer = ({ token , location }) => {
    const [ state, setState ] = useState({ message: '', username: '' });
    const [ userData, setUserData ] = useState();
    const [ chat, setChat ] = useState([]);
    const [ name, setName ] = useState('');
    const [ room, setRoom ] = useState('');


    var socket = io('http://localhost:5000', {transports: ['websocket']});

const Send = (e) => {
    e.preventDefault();
    const { username, message } = state
    socket.emit('chat message', { username, message })
    setState({ message: '', username: username})
    setChat([...chat, {username, message}])
    console.log('CHAT AFTER SEND', chat);
}

const RenderChat = () => {
    return Object.keys(state).map((chatty) => {
        <li key={chatty}>
            <h3>
                {state.username}: <span> {state.message} </span>
            </h3>
        </li>
    })
}

useEffect(() => {

        const data = queryString.parse(location.search)
        console.log(data);

        socket.on('connection', ({ username, message}) => {
            setChat([...chat, { username, message }])
        })

        // socket.on('connect', function () {
        //     socket.emit('chat message', ({ username, message }) => {
        //         socket.emit('chat message', { username, message })
        //         setChat([...chat, { username, message }])
        //     });
        // });

        axios.post('http://localhost:5000/accounts/me', {
        token: token,
        })
            .then(response => {
            setUserData(response.data);
            setState({ username: response.data.username })
    })

}, [])

    if (!userData) {
    return <p> Loading... </p>
}

    if (!token) {
    return <Redirect to='/' />
}

    return (
        <>
            <div className='multibody' >
                <div>
                    <h1> HELLOOOO?? </h1>
                        {/* {chat.map(msg => {
                            <li>
                                <h3>
                                    {msg.username} <span> {msg.message} </span>
                                </h3>
                            </li>
                        })} */}
                        {RenderChat()}
                </div>

                <form className='form' onSubmit={Send}>

                <input required
                    className='input'  
                    type='text'
                    autoComplete='off'
                    id=''
                    placeholder='What would you like to say?'
                    value={state.message}
                    onChange={e => { setState({...state, message: e.target.value})}}
                    >
                </input>
                <button className='bt'>
                    Send
                </button>
                </form>
            </div>
        </>
    );
}

export default Multiplayer;
