import { Redirect, useLocation } from "react-router-dom";
import { io } from 'socket.io-client'
import { useState, useEffect } from "react";

let socket;

const Multiplayer = ({ token }) => {
    const [ username, setUsername ] = useState('')
    const [ roomName, setRoomName ] = useState('');
    const [ message, setMessage ] = useState();
    const [ messages, setMessages ] = useState([{ user:'', text:'' }]);
    const ENDPOINT = 'http://localhost:5000';

    const location = useLocation();
    const socket = io(ENDPOINT, {transports: ['websocket'], upgrade: false});

const sendMessage = (e) => {
    e.preventDefault();
    console.log(message);

    if (message) {
        socket.emit('sendMessage', { user: username, text: message }, roomName)
        setMessage('')
    };
}

// -----------------------JOIN/DISCONNECT------------------>
useEffect(() => {
    const socket = io(ENDPOINT, {transports: ['websocket'], upgrade: false});
    ;
    const { name, username } = location
    setUsername(username);
    setRoomName(name);

    socket.emit('join', { username, roomName }, () => {

    });

    return () => {
        socket.emit('disconnected', { username })
        socket.off();
    }

}, [ENDPOINT, location, !username, !roomName]);

// -------------SEND MESSAGE/ADD TO MESSAGES--------------->

useEffect(() => {
    socket.on('message', (message) => {
        setMessages([...messages, {user: message.username, text: message.text }]);
    });
}, [messages]);

// ----------------------------------------->


    if (!location.name) {
        return <Redirect to='/room' />
    }

    if (!token) {
    return <Redirect to='/' />
    }


    return (
        <div className='outerContainer'>
            <div className='multiContainer'>
                <div className='infoBar'>
                    <div className='leftInnerContainer'>
                        {/* img */}
                        <h3>
                            {roomName}
                        </h3>   
                    </div>
                    {/* <div>
                        { messages.map((message, i) => {
                            <div key={i}>
                                <h5>
                                    {message.username}
                                </h5>
                                <p>
                                    {message.message}  
                                </p>  
                            </div>
                        })}
                    </div> */}
                    <form className='formz'>
                        <input className='inputz'
                            value={message}
                            onChange={e => setMessage(e.target.value)}
                            onKeyPress={e => e.key === 'Enter' ? sendMessage(e) : null}
                            placeholder='What would you like to say?'
                            >
                        </input>
                        <button className='sendButton'
                            onClick={(e) => { sendMessage(e)}}
                            >
                            Send
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Multiplayer;
