 import { Redirect } from "react-router-dom";
//  import { useState } from "react";

 const PlayNow = ({ token }) => {
    // const [ multiplayer, setMultiplayer ] = useState(false);
    // const [ single, setSingle ] = useState(false);

    const Multi = (e) => {
        e.preventDefault();
        return <Redirect to='/multiplayer' />        
    }

    const Single = (e) => {
        e.preventDefault();
        return <Redirect to='/play' />
    }

    if (!token) {
        return <Redirect to='/' />
    }

     return (
        <>
            <div className='container'>
                <div className='card'>
                    <div className='image'>
                        {/* upload image */}
                    </div>
                    <div className='description'>
                        <h3>
                            Single Player
                        </h3>
                        <p>
                            Play against 5 AI villagers and 1 AI Witch as the werewolf!
                        </p>
                    </div>
                    <button className='btn btn-background-circle'
                        onClick={Single}
                            >
                            Single
                    </button>
                </div>
                <div className='multiplayer'>
                    <button className='btn btn-background-circle'
                        onClick={Multi}
                            >
                            Multiplayer
                    </button>
                    <p>
                        Play against 
                    </p>
                </div>
            </div>
        </>
     );
 }

 export default PlayNow;
