import { Redirect, useLocation } from "react-router-dom";
import { io } from 'socket.io-client'
import { useState, useEffect } from "react";

let socket;

const Multiplayer = ({ token }) => {
    const [ username, setUsername ] = useState('')
    const [ roomName, setRoomName ] = useState('');
    const [ message, setMessage ] = useState('');
    const [ messages, setMessages ] = useState([]);
    const ENDPOINT = 'http://localhost:5000';

    const location = useLocation();
    const socket = io(ENDPOINT, {transports: ['websocket'], upgrade: false});

const sendMessage = (e) => {
    e.preventDefault();

    if (message) {
        socket.emit('sendMessage', message, username, roomName, () => {
            setMessage('');
        });
    };
}

console.log(messages);
// -----------------------JOIN/DISCONNECT------------------>
useEffect(() => {
    const socket = io(ENDPOINT, {transports: ['websocket'], upgrade: false});
    ;
    const { name, username } = location
    setUsername(username);
    setRoomName(name);

    socket.emit('join', { username, roomName }, () => {

    });

    return () => {
        socket.emit('disconnected', { username })
        socket.off();
    }

}, [ENDPOINT, location, !username, !roomName]);

// -------------SEND MESSAGE/ADD TO MESSAGES--------------->

useEffect(() => {
    socket.on('message', (message) => {
        setMessages([...messages, message]);
    });
}, [messages]);

// ----------------------------------------->


    if (!location.name) {
        return <Redirect to='/room' />
    }

    if (!token) {
    return <Redirect to='/' />
    }


    return (
        <div className='infoBar'>
            <div className='leftInnerContainer'>
                {/* img */}
                <input value={message}
                    onChange={e => setMessage(e.target.value)}
                    onKeyPress={e => e.key === 'Enter' ? sendMessage(e) : null}
                    >

                </input>
            </div>
        </div>
    );
}

export default Multiplayer;
