import { Redirect, useLocation } from "react-router-dom";
import { io } from 'socket.io-client'
import { useState, useEffect } from "react";
import axios from "axios";

const Multiplayer = ({ token }) => {
    const [ state, setState ] = useState({ message: '', username: '' });
    const [ userData, setUserData ] = useState();
    const [ chat, setChat ] = useState([]);
    const [ roomName, setRoomName ] = useState('');
    const [ roomID, setRoomID ] = useState('');
    const ENDPOINT = 'http://localhost:5000';

    const location = useLocation();
    const socket = io(ENDPOINT, {transports: ['websocket']});


const sendMessage = (e) => {
    e.preventDefault()
    // const { username, message } = state
    // // socket.emit('chat message', { username, message })
    // setState({ message: '', username: username})
    // setChat([...chat, {username, message}])
    // console.log('CHAT AFTER SEND', chat);
    if (state) {
        socket.emit('sendMessage', state.message, () => {
            setState({ message: '', username: userData.username})

        })
    }
}

console.log(state, chat);

const RenderChat = () => {
    return Object.keys(state).map((chatty) => {
        <li key={chatty}>
            <h3>
                {state.username}: <span> {state.message} </span>
            </h3>
        </li>
    })
}

useEffect(() => {

    const socket = io(ENDPOINT, {transports: ['websocket']});

    setRoomName(location.name);
    setRoomID(location.room)
    setState({ username: state.username })

    socket.emit('join', { username: state.username, roomName, roomID }, () => {

    });

    return () => {
        socket.emit('disconnected', { username: state.username });

        socket.off();
    }

}, [ENDPOINT, location]);

useEffect(() => {

    const socket = io(ENDPOINT, {transports: ['websocket']});
    socket.on('message', (message) => {
        setChat([...chat, message])
    })
}, [chat]);



    if (!location.name) {
        return <Redirect to='/room' />
    }

    if (!userData) {
    return <p> Loading... </p>
}

    if (!token) {
    return <Redirect to='/' />
}



    return (
        <>

        </>
    );
}

export default Multiplayer;
