import { Redirect } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

const PostGame = ({ win, userKills }) => {

    const [ userData, setUserData ] = useState();
    const [ totalGames, setTotalGames] = useState();
    const [ totalWins, setTotalWins ] = useState();
    const [ totalKills, setTotalKills] = useState();

    let gameResults = 0

    if (win === true) {
        gameResults = 1
    }

    const games = 1

   setTotalGames(games + userData.total_games);
   setTotalWins(gameResults + userData.total_wins);
   setTotalKills(userKills + userData.total_kills);

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        }).then(response => {
            setUserData(response.data);
        })
    },

        axios.put('http://localhost:5000/postgame', {
            token: token,
            total_games: totalGames,
            total_wins: totalWins,
            totalKills: totalKills
        })
    , []);

    if (!token) {
        return <Redirect to='/' />
    }

    return (
        <>
            {
                win?
                    <h3>
                        Congratulations you won!
                    </h3>
                :
                    <h3>
                        You got caught!
                    </h3>
            }

            <h3>
                You eliminated {userKills} players! 
            </h3>

            <p>
                Your account has been updated!
            </p>
        </>
    );
}

export default PostGame;
