import { Redirect } from "react-router";
import { io } from 'socket.io-client'
import { useState, useEffect } from "react";
import axios from "axios";

const socket = io('http://localhost:5000', { cors: {
    origin: "http://localhost:5000",
    credentials: true
  }, transports: ['websocket'] });


const Multiplayer = ({ token }) => {
    const [ message, setMessage ] = useState({ message: '', username: '' });
    const [ userData, setUserData ] = useState();
    const [ chat, setChat ] = useState();


const Send = (e) => {
    e.preventDefault();
    const [ username, message ] = message
    socket.emit('chat message', { username, message })
    setMessage({ message: '', username: userData.username})
}

const RenderChat = () => {
    return chat.map(({username, message}, key) => {
        <li key={key}>
            <h3>
                {username}: <span> {message} </span>
            </h3>
        </li>
    })
}

useEffect(() => {

    axios.post('http://localhost:5000/accounts/me', {
        token: token,
    }).then(response => {
        setUserData(response.data);
        setMessage({ message: '', username: response.data.username});
        // socket.emit('chat message', ({ username, message }) => {
        //     setChat([...chat, { username, message }])
        // })
    })
}, [])

if (!userData) {
    return <p> Loading... </p>
}

if (!token) {
    return <Redirect to='/' />
}

    return (
        <>
            <div className='multibody' >
                <div>
                    <ul className='messages'>
                        {RenderChat}
                    </ul>

                </div>

                <form className='form' onSubmit={Send}>

                <input required
                    className='input'  
                    type='text'
                    autoComplete='off'
                    id=''
                    placeholder='What would you like to say?'
                    value={message.message}
                    onChange={e => { setMessage({...message, message: e.target.value})}}
                    >
                </input>
                <button className='bt'>
                    Send
                </button>
                </form>
            </div>
        </>
    );
}

export default Multiplayer;
