import React, {useState} from 'react';
// import SignUp from './SignUp';
import axios from 'axios';

const SignIn = () => {

    const [logusername, setLogUsername] = useState('');
    const [logpassword, setLogPassword] = useState('');
    const [isPending, setIsPending] = useState(false);

    const SubmitSignIn = async (e) => {
        e.preventDefault();
        setIsPending(true);

        axios.post('http://localhost:5000/signin', {
                    username: logusername,
                    password: logpassword
               })
               .then(response => {
                   setIsPending(false);
                   console.log(response.data);
               })
            }
            // const response = await fetch('http://localhost:5000/signin', {
            //     method: 'POST',
            //     credentials: 'include',
            //     headers: {'Content-Type': 'application/json'},
            //     body: JSON.stringify({
            //         username: logusername,
            //         password: logpassword
            //     })
    //             })
    //             .then(response => {
    //                 if (!response.ok) {
    //                     throw Error('Could not fetch data for that resource!');
    //                 }
    //                 setIsPending(false);
    //                 return response.JSON();
    //             })
    //             .catch(error => {
    //                 console.log(error.message);
    //             })
    //             console.log(response.data);
    //             alert('Account Created!');
    //             window.location = '/';
    // }

    return(
            <form className="create" onSubmit={SubmitSignIn}>
                <h1 className="text-center mt-5"> Log In!</h1>
                <div>
                    <label htmlFor='username'>Username</label>
                    <input 
                    type='text' 
                    name='username' 
                    id='username' 
                    placeholder='Enter your username'
                    value={logusername.username}
                    onChange={e => setLogUsername(e.target.value)}></input>
                </div>
                <div>
                    <label htmlFor='password'>Password</label>
                    <input 
                    type='password' 
                    name='password' 
                    id='password' 
                    placeholder='Enter your password'
                    value={logpassword.password}
                    onChange={e => setLogPassword(e.target.value)}></input>
                </div>
                { !isPending && <button className="signin-button" >Sign In</button> }
                { isPending && <button className="signin-button" disabled>Signing in....</button> }
            </form>
    )
}

export default SignIn;
