import React, {Fragment, useEffect, useState} from 'react';
import { Link, Redirect } from 'react-router-dom';

const AccountShowList = ({ token }) => {

    const [accounts, setAccounts] = useState([]);

    const getAccounts = async() => {
        try {
            const response = await fetch('http://localhost:5000/accounts');
            const jsonData = await response.json();

            setAccounts(jsonData);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getAccounts();
    }, []);

    if (!token) {
        return <Redirect to='/' />
    }

    return (
        <>
            <th> 
                Username:
            </th>
            <th>
                Games:
            </th>
            <th>
                Wins:
            </th>
            <th>
                Kills:
            </th>
            <tbody>
                {accounts.map(account => (
                <tr className="leaderboard-accounts" key={account.account_id}>
                    <td> <Link to={`/accounts/${account.account_id}`}> {account.username} </Link> </td>
                    <td>{account.total_games}</td>
                    <td>{account.total_wins}</td>
                    <td>{account.total_kills}</td>
                </tr> 
                ))}
            </tbody>

        </>
    );
}


export default AccountShowList;
