import { useEffect, useState } from "react";
import axios from "axios";

const Play = ({ token }) => {
    const [ userData, setUserData ] = useState();
    const [ inGame, setInGame ] = useState(false);
    const [ nightPhase, setNightPhase ] = useState(false);
    const [ morningPhase, setMorningPhase ] = useState(false);
    const [ disabled, setDisabled] = useState(false);
    const [ userKilled, setUserKilled] = useState(false);
    const [ playersRemaining, setPlayersRemaining] = useState(6);
    const [ userKills, setUserKills] = useState(0);
    const [ witch, setWitch] = useState(0);
    const [ alive, setAlive] = useState(true);

    const Start = () => {
        setInGame(true);
        setMorningPhase(true);
        setNightPhase(false); 
        setDisabled(false);
        setUserKilled(false);
        setWitch(0);
        setAlive(true);
    }

    const Select = (e) => {
        const selected = e.target.id;

        if (witch === 0) {
            let healer = Math.floor((Math.random() * playersRemaining));
            setWitch(healer);
        }

        if ( userKilled === false || playersRemaining >= 2 ) {
            if ( morningPhase === true ) { 
                let userVote = parseInt(selected, 10);
                let votes = []; 

                while (votes.length < playersRemaining) {
                    const random = Math.floor((Math.random() * 7) + 1);
                    votes.push(random);
                } 
                votes.push(userVote);

                let highestReoccurence = 1;
                let counter = 0;
                let mostVotes = [];

                for (let i = 0; i <= votes.length; i++) {
                    for (let j = i; j <votes.length; j++) {
                        if (votes[i] === votes[j]) {
                            counter++;
                        } else if ( highestReoccurence < counter) {
                            highestReoccurence = counter
                            mostVotes = votes[i];
                        }
                    }
                    counter = 0;
                }
                    if (mostVotes === 1) {
                        alert('You have been caught');
                    }

                const hanged = document.getElementById(mostVotes); 
                console.log(hanged)
                hanged.disabled = true;

                highestReoccurence = 1
                counter = 0;
                mostVotes = [];
                votes = [];
                setPlayersRemaining(playersRemaining - 1)
                setMorningPhase(false);
                setNightPhase(true);
            }
            else if (nightPhase === true){
                const eliminate = e.target.id
                const witchProtects = Math.floor((Math.random() * playersRemaining));
                const attackedUser = document.getElementById(witchProtects);

                if (eliminate === witchProtects) {
                    alert(`Witch has healed player ${attackedUser} from a werewolf attack!`);
                }
                else if (eliminate !== witchProtects) {
                    attackedUser.disabled = true;
                    setPlayersRemaining(playersRemaining - 1);
                    setUserKills(userKills + 1);
                } 
                else {
                    alert('somethings wrong with your night-phase, eliminate/witch statements');
                }

                setMorningPhase(true);
                setNightPhase(false);
            }
            else {
                alert('Something wrong with phases...');
            }
        }
        else if (playersRemaining === 0) {
            alert('Congratulations, you win!');
        }
        else if (userKilled === true) {
            alert('You lost...');
        }
        else {
            alert('Something is wrong with the game..');
        }
    }

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        })
        .then(response => {
            setUserData(response.data);
        });
    }, [])


    return(
        <>
            { !inGame?
                <button 
                    className='start-button'
                    onClick={Start}
                    >
                    Start
                </button>
            : 
            <>  
                { !morningPhase? 
                <>
                    <h1> 
                        NIGHT-PHASE
                    </h1>
                    <h2>
                        Who would you like to eliminate?
                    </h2>
                </>
                :
                <>
                    <h1>
                        MORNING-PHASE
                    </h1>
                    <h2>
                        Vote who you would like to "vote" as the "werewolf?"
                    </h2>
                </>
                }
                <h3 className='player'
                    id={0}
                    > 
                    {userData.username} players remaining: {playersRemaining} kills: {userKills} witch: {witch}
                </h3>
                <button 
                    className='player'
                    id={1}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU1
                </button>
                <button 
                    className='player'
                    id={2}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU2
                </button>
                <button 
                    className='player'
                    id={3}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU3
                </button>
                <button 
                    className='player'
                    id={4}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU4
                </button> 
                <button 
                    className='player'
                    id={5}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU5
                </button>
                <button 
                className='player'
                id={6}
                disabled={disabled}
                onClick={Select}
                value={alive}
                >
                Morning-CPU6
                </button>
            </>     
            }


        </>
    );
}


export default Play;
