import React, {Fragment, useEffect, useState} from 'react';
import { Link } from 'react-router-dom';

const AccountShowList = () => {

    const [accounts, setAccounts] = useState([]);

    const getAccounts = async() => {
        try {
            const response = await fetch('http://localhost:5000/accounts');
            const jsonData = await response.json();

            setAccounts(jsonData);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getAccounts();
    }, []);

    return (
        <Fragment>
            {" "}
            <table className="table mt-5 text-center">
    <thead>
      <tr>
        <th>Username:</th>
        <th>Games:</th>
        <th>Wins:</th>
        <th>Kills:</th>
      </tr>
    </thead>
    <tbody>
        {accounts.map(account => (
        <tr className="leaderboard-accounts" key={account.account_id}>
             <td> <Link to={`/accounts/${account.id}`}> <h5>{account.username}</h5> </Link> </td>
             <td><p>{account.total_games}</p></td>
             <td><p>{account.total_wins}</p></td>
             <td><p>{account.total_kills}</p></td>
         </tr> 
        ))}
    </tbody>
  </table>
        </Fragment>
    )
}


export default AccountShowList;
