import { useEffect, useState } from "react";
import axios from "axios";

const Play = ({ token }) => {
    const [ userData, setUserData ] = useState();
    const [ inGame, setInGame ] = useState(false);
    const [ nightPhase, setNightPhase ] = useState(false);
    const [ morningPhase, setMorningPhase ] = useState(false);
    const [ alive, setAlive ] = useState(true);
    const [ disabled, setDisabled] = useState(false);

    const Start = () => {
        setInGame(true);
        setMorningPhase(true);
        setNightPhase(false); 
        setAlive(true);
        setDisabled(false);
    }

    const SelectStageMorning = (e) => {
        let userVote = e.target.id
        let votes = []; 
        while (votes.length < 6) {
            const random = Math.floor((Math.random() * 6) + 1);
            votes.push([random]);
        } 
        votes.push(userVote.split(''));
        console.log(votes);
    }

    const SelectStageNight = () => {
        console.log();
   }

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        })
        .then(response => {
            setUserData(response.data);
        });
    }, [])

    const players = [];
    while (players.length < 6) {
        const random = Math.floor((Math.random() * 6) + 1);
        if (players.indexOf(random) === -1) {
            players.push([random]);
        }   
    }

    return(
        <>
            { !inGame?
                <button 
                    className='start-button'
                    onClick={Start}
                    >
                    Start
                </button>
            : 
            <>
                { !nightPhase && 
                <>
                    <h2>
                        Vote who you would like to frame as the 'werewolf'
                    </h2>
                    <h3 className='user'
                        > 
                        {userData.username}
                    </h3>
                    <button 
                        className='morning-cpu'
                        id={players.indexOf(1)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageMorning}
                        >
                        Morning-CPU1
                    </button>
                    <button 
                        className='morning-cpu'
                        id={players.indexOf(2)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageMorning}
                        >
                        Morning-CPU2
                    </button>
                    <button 
                        className='morning-cpu'
                        id={players.indexOf(3)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageMorning}
                        >
                        Morning-CPU3
                    </button>
                    <button 
                        className='morning-cpu'
                        id={players.indexOf(4)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageMorning}
                        >
                        Morning-CPU4
                    </button> 
                    <button 
                        className='morning-cpu'
                        id={players.indexOf(5)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageMorning}
                        >
                        Morning-CPU5
                    </button>
                    <button 
                        className='morning-cpu'
                        id={players.indexOf(6)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageMorning}
                        >
                        Morning-CPU6
                    </button>
                </>        
                }
                { !morningPhase &&
                <>
                    <h2 className='user'>
                        {userData.username}      
                    </h2>
                    <button 
                        className='night-cpu'
                        id={players.indexOf(1)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageNight}
                        >
                        Night-CPU1
                    </button>
                    <button 
                        className='night-cpu'
                        id={players.indexOf(2)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageNight}
                        >
                        Night-CPU2
                    </button>
                    <button 
                        className='night-cpu'
                        id={players.indexOf(3)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageNight}
                        >
                        Night-CPU3
                    </button>
                    <button 
                        className='night-cpu'
                        id={players.indexOf(4)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageNight}
                        >
                        Night-CPU4
                    </button> 
                    <button 
                        className='night-cpu'
                        id={players.indexOf(5)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageNight}
                        >
                        Night-CPU5
                    </button>
                    <button 
                        className='night-cpu'
                        id={players.indexOf(6)}
                        value={alive}
                        disabled={disabled}
                        onClick={SelectStageNight}
                        >
                        Night-CPU6
                    </button>
                </>
                }
            </>
            }
        </>
    );
}

export default Play;
