import { useParams } from "react-router";
import { useEffect, useState } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";

const AccountShow = ({ token }) => {
    const { id } = useParams();
    const [ account, setAccount ] = useState();

    useEffect(() => {
        axios.post(`http://localhost:5000/accounts/${id}`, {
            account_id: id
        })
        .then(response => {
            setAccount(response.data);
        });
    }, [])

    if (!token) {
        return <Redirect to='/' />
    }

    return (
        <div>
            { !account? <p> Loading data... </p> : 
            <> 
                <h1>
                    {account.username}
                </h1>

                <h3>
                    Games: {account.total_games || "Insufficient Data"}
                </h3> 

                <h3>
                    Wins: {account.total_wins || "Insufficient Data" }
                </h3> 
                    { account.total_games && account.total_wins &&  
                <h3>
                    Win%: {(account.total_wins/account.total_games * 100).toFixed(1)}
                </h3>  }

                <h3>
                    Kills: {account.total_kills || "Insufficient Data"}
                </h3> 
            </> 
            } 
        </div>
    )
}

export default AccountShow;
