import { useEffect, useState } from "react";
import axios from "axios";
import { useHistory, Redirect } from "react-router-dom";
import P1moon from './img/p1moon.png';
import P2moon from './img/p2moon.png';
import P3moon from './img/p3moon.png';
import P4moon from './img/p4moon.png';
import P5moon from './img/p5moon.png';
import P6moon from './img/p6moon.png';

const Play = ({ token, inGame, setInGame, setWin, setUserKills, userKills }) => {

    const [ userData, setUserData ] = useState();
    const [ nightPhase, setNightPhase ] = useState(false);
    const [ morningPhase, setMorningPhase ] = useState(false);
    const [ disabled, setDisabled ] = useState(false);
    const [ playersRemaining, setPlayersRemaining ] = useState(7);
    const [ witch, setWitch ] = useState(0);
    const [ alive, setAlive ] = useState(true);
    const [ consoleLog, setConsoleLog ] = useState();
    const [ logVotes, setLogVotes ] = useState();
    let history = useHistory();

    // const p1 = document.getElementById(1);
    // const p2 = document.getElementById(2);
    // const p3 = document.getElementById(3);
    // const p4 = document.getElementById(4);
    // const p5 = document.getElementById(5);
    // const p6 = document.getElementById(6);


    const Start = () => {
        setInGame(true);
        setMorningPhase(true);
        setNightPhase(false); 
        setDisabled(false);
        setWitch(0);
        setAlive(true);
        setConsoleLog();
        setPlayersRemaining(7);
        setUserKills(0);
        setConsoleLog();
        setLogVotes();
        setWin(null);
    }

    const Select = (e) => {
        const selected = e.target.id;
        let cpuVotes = playersRemaining - 2;

        if (witch === 0) {
            let healer = Math.floor((Math.random() * playersRemaining + 1));
            setWitch(healer);
        }

        if ( playersRemaining >= 3 ) {
            if ( morningPhase === true ) { 
                let userVote = parseInt(selected, 10);
                let votes = []; 

                for (let i = 0; i <= cpuVotes; i++) {
                    let random = Math.floor((Math.random() * playersRemaining + 1));
                    votes.push(random);
                }
                votes.push(userVote);
                setLogVotes(votes);

                let highestReoccurence = 1;
                let counter = 0;
                let mostVotes = [];

                for (let i = 0; i <= votes.length; i++) {
                    for (let j = i; j <votes.length; j++) {
                        if (votes[i] === votes[j]) {
                            counter++;
                        }

                        if ( highestReoccurence < counter) {
                            highestReoccurence = counter
                            mostVotes = [votes[i]];
                        } else if ( highestReoccurence === counter) {
                          if(!mostVotes.includes(votes[i])) mostVotes.push(votes[i])
                        }
                    }
                    counter = 0;
                }

                if (mostVotes.length === 2) {
                    setConsoleLog(`Two players are tied for votes, there will be no execution today!`);
                }
                else if (mostVotes.length >= 3) {
                    setConsoleLog(`Multiple ties, there will be no execution today!`);
                } 
                else if (mostVotes.length === 1) {
                    let parseVotes = parseInt(mostVotes, 10);

                    if (parseVotes === 7) {
                        setWin(false);
                        history.push('/postgame');
                    } 
                    else {
                        const hanged = document.getElementById(parseVotes); 
                        hanged.disabled = true;
                        hanged.value = false;
                        setConsoleLog(`Player ${parseVotes} has been hanged`);
                    }
                }
                else {
                    alert('Something wrong with vote.length');
                }

                highestReoccurence = 1
                counter = 0;
                mostVotes = [];
                votes = [];
                setPlayersRemaining(playersRemaining - 1)
                setMorningPhase(false);
                setNightPhase(true);
            }
            else if (nightPhase === true) {
                const eliminate = e.target.id
                const userEliminates = parseInt(eliminate, 10);
                const playerEliminated = document.getElementById(userEliminates);
                const witchProtects = Math.floor((Math.random() * playersRemaining));
                const witchalive = document.getElementById(witch);

                if (witchalive.value = true) {
                    if (userEliminates === witchProtects) {
                        setConsoleLog(`Witch has healed player ${witchProtects} from a werewolf attack!`);
                    }
                    else if (userEliminates !== witchProtects) {
                        playerEliminated.disabled = true;
                        playerEliminated.value = false;
                        setPlayersRemaining(playersRemaining - 1);
                        setUserKills(userKills + 1);
                        setConsoleLog(`The werewolf eliminated player ${userEliminates}`);
                    } 
                    else {
                        alert('somethings wrong with your night-phase, if witch is alive statements');
                    } 
                }
                else if (witchalive.value = false) {
                    playerEliminated.disabled = true;
                    playerEliminated.value = false;
                    setPlayersRemaining(playersRemaining - 1);
                    setUserKills(userKills + 1);
                    setConsoleLog(`The werewolf eliminated player ${userEliminates}`);
                }
                else {
                    alert('something wrong with witch alive condition statement');
                }

                setLogVotes();
                setMorningPhase(true);
                setNightPhase(false);
            }
            else {
                alert('Something wrong with phases...');
            }
        }
        else if (playersRemaining === 2) {
            setWin(true);
            history.push('/postgame');
        }
        else {
            alert('Something is wrong with the game..');
        }
    }

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        })
        .then(response => {
            setUserData(response.data);
        });
    }, [])

    if (!token) {
        return <Redirect to='/' />
    }

    return(
        <>
            { !inGame?
                <div className="play">
                    <h1>
                        Good luck!
                    </h1>
                    <button 
                        className='start-button'
                        onClick={Start}
                        >
                        Play
                    </button>
                </div>
            : 
            <>  
                <h3 className='player7'
                    id={7}
                    > 
                    {userData.username}
                </h3>

                <h5 className="console"
                    >
                    {consoleLog}
                </h5>

                {/* { !morningPhase? 
                <div className='phases'>
                    <h2 className='phase'> 
                        NIGHT-PHASE
                    </h2>
                    <h3 className='who'>
                        Who would you like to eliminate?
                    </h3>
                </div>
                :
                <div className='phases'>
                    <h2 className='phase'>
                        MORNING-PHASE
                    </h2>
                    <h3 className="who">
                        Vote who you would like to "frame" as the "werewolf"
                    </h3>
                </div>
                } */}
                <div className='container'>

                    <div className='card'>
                        <div className='image'>
                            <img src={P1moon} alt='player 1' />
                        </div>
                        <div className='description'>
                            <h3>
                                Simon
                            </h3>
                            <p>
                                This gentleman reminds you of an unavoidable bloodhound. His luxurious, straight, night-black hair is worn in a style that reminds you of a crown. 
                                He is very tall, his skin is light. He has a small nose and a weak chin. His wardrobe is tight.
                            </p>
                        </div>
                        <button 
                            className='playbutton'
                            id={1}
                            disabled={disabled}
                            onClick={Select}
                            value={alive}
                            >
                            Player 1
                        </button>
                    </div>

                    <div className='card'>
                        <div className='image'>
                            <img src={P1moon} alt='player 1' />
                        </div>
                        <div className='description'>
                            <h3>
                                Simon
                            </h3>
                            <p>
                                This gentleman reminds you of an unavoidable bloodhound. His luxurious, straight, night-black hair is worn in a style that reminds you of a crown. 
                                He is very tall, his skin is light. He has a small nose and a weak chin. His wardrobe is tight.
                            </p>
                        </div>
                        <button 
                            className='playbutton'
                            id={2}
                            disabled={disabled}
                            onClick={Select}
                            value={alive}
                            >
                            Player 2
                        </button>
                    </div>




                    <button 
                        className='playbutton'
                        id={3}
                        disabled={disabled}
                        onClick={Select}
                        value={alive}
                        >
                        Player 3
                    </button>
                    <button 
                        className='playbutton'
                        id={4}
                        disabled={disabled}
                        onClick={Select}
                        value={alive}
                        >
                        Player 4
                    </button> 
                    <button 
                        className='playbutton'
                        id={5}
                        disabled={disabled}
                        onClick={Select}
                        value={alive}
                        >
                        Player 5
                    </button>
                    <button 
                    className='playbutton'
                    id={6}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Player 6
                    </button>

                </div>
            </>     
            }
        </>
    );
}


export default Play;
