import { useEffect, useState } from "react";
import axios from "axios";

const Play = ({ token }) => {
    const [ userData, setUserData ] = useState();
    const [ inGame, setInGame ] = useState(false);
    const [ nightPhase, setNightPhase ] = useState(false);
    const [ morningPhase, setMorningPhase ] = useState(false);
    const [ alive, setAlive ] = useState(true);
    const [ disabled, setDisabled] = useState(false);
    const [ userKilled, setUserKilled] = useState(false);

    const playersRemaining = 7;
    const userKills = 0;

    const Start = () => {
        setInGame(true);
        setMorningPhase(true);
        setNightPhase(false); 
        setAlive(true);
        setDisabled(false);
        setUserKilled(false);
    }

    const Select = (e) => {

        if ( userKilled === false || playersRemaining >= 2 ) {
            if ( morningPhase === true ) { 
                let userVote = parseInt(e.target.id, 10);
                let votes = []; 

                while (votes.length < playersRemaining) {
                    const random = Math.floor((Math.random() * 7) + 1);
                    votes.push(random);
                } 
                votes.push(userVote);

                let highestReoccurence = 1;
                let counter = 0;
                let mostVotes = [];

                for (let i = 0; i <= votes.length; i++) {
                    for (let j = i; j <votes.length; j++) {
                        if (votes[i] === votes[j]) {
                            counter++;
                        } else if ( highestReoccurence < counter) {
                            highestReoccurence = counter
                            mostVotes = votes[i];
                        }
                    }
                    counter = 0;
                }
                const hanged = document.getElementById(mostVotes); 
                console.log(hanged);
                hanged.value = setAlive(false);
                hanged.disabled = true;
                highestReoccurence = 1
                counter = 0;
                mostVotes = [];
                votes = [];
                setMorningPhase(false);
                setNightPhase(true);
            }
            else {
                setMorningPhase(true);
                setNightPhase(false);
            }
        }
        else if (playersRemaining === 0) {
            alert('Congratulations, you win!');
        }
        else if (userKilled === true) {
            alert('You lost...');
        }
        else {
            alert('Something is wrong..');
        }
    }

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        })
        .then(response => {
            setUserData(response.data);
        });
    }, [])

    // const players = [];
    // while (players.length < 7) {
    //     const random = Math.floor((Math.random() * 7) + 1);
    //     if (players.indexOf(random) === -1) {
    //         players.push(random);
    //     }   
    // }

    return(
        <>
            { !inGame?
                <button 
                    className='start-button'
                    onClick={Start}
                    >
                    Start
                </button>
            : 
            <>  
                { !morningPhase? 
                    <h2>
                        Who would you like to eliminate?
                    </h2>
                :
                    <h2>
                        Vote who you would like to frame as the "werewolf"
                    </h2>
                }
                <h3 className='user'
                    id={1}
                    value={alive}
                    > 
                    {userData.username}
                </h3>
                <button 
                    className='morning-cpu'
                    id={2}
                    value={alive}
                    disabled={disabled}
                    onClick={Select}
                    >
                    Morning-CPU1
                </button>
                <button 
                    className='morning-cpu'
                    id={3}
                    value={alive}
                    disabled={disabled}
                    onClick={Select}
                    >
                    Morning-CPU2
                </button>
                <button 
                    className='morning-cpu'
                    id={4}
                    value={alive}
                    disabled={disabled}
                    onClick={Select}
                    >
                    Morning-CPU3
                </button>
                <button 
                    className='morning-cpu'
                    id={5}
                    value={alive}
                    disabled={disabled}
                    onClick={Select}
                    >
                    Morning-CPU4
                </button> 
                <button 
                    className='morning-cpu'
                    id={6}
                    value={alive}
                    disabled={disabled}
                        onClick={Select}
                        >
                        Morning-CPU5
                    </button>
                    <button 
                    className='morning-cpu'
                    id={7}
                    value={alive}
                    disabled={disabled}
                    onClick={Select}
                    >
                    Morning-CPU6
                </button>
            </>     
            }


        </>
    );
}


export default Play;
