                    {userData.username}
                </h1>

                <h3>
                    Games: {userData.total_games || "Insufficient Data"}
                </h3> 

                <h3>
                    Wins: {userData.total_wins || "Insufficient Data" }
                </h3> 
                    { userData.total_games && userData.total_wins &&  
                <h3>
                    Win%: {(userData.total_wins/userData.total_games * 100).toFixed(1)}
                </h3>  }

                <h3>
                    Kills: {userData.total_kills || "Insufficient Data"}
                </h3> 
            </> 
            } 
        </div>
    )
}

export default AccountShow;
