import React, { useEffect, useState } from 'react';
import axios from 'axios';


 const AccountDetails = ({token}) => {
    const [ userData, setUserData ] = useState();

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        }).then(response => {
            setUserData(response.data);
        })
    }, [])


    return (
        <div>
            { !userData? <p> Loading data... </p> : 
            <> 
                <h1>
                    {userData.username}
                </h1>

                <h3>
                    Games: {userData.total_games || 0}
                </h3> 

                <h3>
                    Wins: {userData.total_wins || 0 }
                </h3> 
                    { userData.total_games && userData.total_wins &&  
                <h3>
                    Win%: {userData.total_wins/userData.total_games}
                </h3>  }

                <h3>
                    Kills: {userData.total_kills || 0}
                </h3> 
            </> 
            } 
        </div>
    )
}

export default AccountDetails;
