import React, {useState, useEffect} from 'react';

const SignUp = () => {

    const [values, setValues] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: ''
    });

    const [errors, setErrors] = useState({})

    const handleChanges = event => {
        const {name, value} = event.target
        setValues({
            ...values,
            [name]: value
        })
    }

    const Submit = async () => {
        try {
            const response = await fetch('http://localhost:5000/accounts');
            const jsonData = await response.json();
            setValues(jsonData)
        } catch (error) {
            console.log(error);
        }
    }

    return(
        <fragment>
            <form className="sign Up" onSubmit={Submit}>
                <h1 className="text-center mt-5"> Sign up!</h1>
                <h1>Get started with us today! Create your account by filling out the information below!</h1>
                <div>
                    <label for='username'>Username</label>
                    <input 
                    type='text' 
                    name='username' 
                    id='username' 
                    placeholder='Enter your username'
                    values={values.username}
                    onChange={handleChanges}></input>
                </div>
                <div>
                    <label for='email'>Email</label>
                    <input type='text' name='email' id='email' placeholder='Enter your email'></input>
                </div>
                <div>
                    <label for='password'>Password</label>
                    <input type='text' name='password' id='password' placeholder='Enter your password'></input>
                </div>
                <div>
                    <label for='confirmPassword'>Confirm Password</label>
                    <input type='text' name='confirmpassword' id='confirmPassword' placeholder='Confirm your password'></input>
                </div>
                <button className="signup-button" type="submit">Sign Up</button>
            </form>
        </fragment>
    )
}

export default SignUp;
