import { useEffect, useState } from "react";
import axios from "axios";

const Play = ({ token }) => {
    const [ userData, setUserData ] = useState();
    const [ inGame, setInGame ] = useState(false);

    const Start = () => {
        setInGame(true);
    }

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        })
        .then(response => {
            console.log(response.data);
            setUserData(response.data);
        });
    }, [])

    return(
        <section className='Game'> 

                <div className='start'>
                    <button 
                        className='start-button'
                        onClick={Start}>
                            Start
                    </button>

                    <button 
                        className='user'>
                            Werewolf
                    </button>

                    <button 
                        className='CPU'>
                            CPU1
                    </button>

                    <button 
                        className='CPU'>
                            CPU2
                    </button>

                    <button 
                        className='CPU'>
                            CPU3
                    </button>

                    <button 
                        className='CPU'>
                            CPU4
                    </button> 

                    <button 
                        className='CPU'>
                            CPU5
                    </button>
                </div> 

        </section>
    );
}

export default Play;
