import React, {useState} from 'react';

const SignIn = () => {

    const [logusername, setLogUsername] = useState('');
    const [logpassword, setLogPassword] = useState('');

    const SubmitSignIn = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/signin', {
                method: 'POST',
                credentials: 'include',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify({
                    username: logusername,
                    password: logpassword
                })
                });
                console.log(response);
        } catch (error) {
            console.log(error);
        }
    }

    return(
            <form className="sign Up" onSubmit={SubmitSignIn}>
                <h1 className="text-center mt-5"> Sign up!</h1>
                <h1>Get started with us today! Create your account by filling out the information below!</h1>
                <div>
                    <label htmlFor='username'>Username</label>
                    <input 
                    type='text' 
                    name='username' 
                    id='username' 
                    placeholder='Enter your username'
                    value={logusername.username}
                    onChange={e => setLogUsername(e.target.value)}></input>
                </div>
                <div>
                    <label htmlFor='password'>Password</label>
                    <input 
                    type='password' 
                    name='password' 
                    id='password' 
                    placeholder='Enter your password'
                    value={logpassword.password}
                    onChange={e => setLogPassword(e.target.value)}></input>
                </div>
                <button className="signin-button" onClick={SubmitSignIn}>Sign In</button>
            </form>
    )
}

export default SignIn;
