import { Redirect } from "react-router";
import io from 'socket.io-client'
import { useState, useEffect } from "react";
import axios from "axios";

const socket = io.connect('http://localhost:5000');

const Multiplayer = ({ token }) => {
    const [ messages, setMessages ] = useState({ message: '', username: '' });
    const [ userData, setUserData ] = useState();
    const [ chat, setChat ] = useState();



const Send = (e) => {
    e.preventDefault();
    const [ username, message ] = messages
    socket.emit('chat message', { username, message })
    setMessages({ message: '', username: userData.username})
}

const renderChat = () => {
    return chat.map(({username, message}, key) => {
        <li key={key}>
            <h3>
                {username}: <span> {message} </span>
            </h3>
        </li>
    })
}

useEffect(() => {
    axios.post('http://localhost:5000/accounts/me', {
        token: token,
    }).then(response => {
        setUserData(response.data);
        setMessage({ message: '', username: response.data.username});
    })
}, [])

if (!userData) {
    return <p> Loading... </p>
}

if (!token) {
    return <Redirect to='/' />
}

    return (
        <>
            <div className='multibody' >
                <div>
                    <ul className='messages' />
                </div>

                <form className='form' onSubmit={Send}>

                <input required
                    className='input'  
                    type='text'
                    autoComplete='off'
                    id=''
                    placeholder='What would you like to say?'
                    value={messages.message}
                    onChange={e => { setMessage(e.target.value)}}
                    >
                </input>
                <button className='bt'>
                    Send
                </button>
                </form>
            </div>
        </>
    );
}

export default Multiplayer;
