import { Redirect } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

const PostGame = ({ token, win, userKills }) => {

    const [ userData, setUserData ] = useState();
    const [ totalGames, setTotalGames] = useState();
    const [ totalWins, setTotalWins ] = useState();
    const [ totalKills, setTotalKills] = useState();

    let gameResults = 0

    if (win === true) {
        gameResults = 1
    }

    const games = 1

   useEffect(() => {
    axios.post('http://localhost:5000/accounts/me', {
        token: token,
    })
    .then(response => {
        setUserData(response.data);
    });
}, [])

setTotalGames(games + userData.total_games);
setTotalWins(gameResults + userData.total_wins);
setTotalKills(userKills + userData.total_kills);

    if (!token) {
        return <Redirect to='/' />
    }

    return (
        <div className='postgame'>
            {
                win?
                    <h3>
                        Congratulations you won!
                    </h3>
                :
                    <h3>
                        You got caught!
                    </h3>
            }

            <h3>
                You eliminated {userKills} players! 
            </h3>

            <h3>
                total games: {totalGames} total wins: {totalWins} total kills: {totalKills}
            </h3>


        </div>
    );
}

export default PostGame;
