import { useEffect, useState } from "react";
import axios from "axios";
import { useHistory, Redirect } from "react-router-dom";


const Play = ({ token, inGame, setInGame, setWin, setUserKills, userKills }) => {

    const [ userData, setUserData ] = useState();
    const [ nightPhase, setNightPhase ] = useState(false);
    const [ morningPhase, setMorningPhase ] = useState(false);
    const [ disabled, setDisabled ] = useState(false);
    const [ userKilled, setUserKilled ] = useState(false);
    const [ playersRemaining, setPlayersRemaining ] = useState(6);
    const [ witch, setWitch ] = useState(0);
    const [ alive, setAlive ] = useState(true);
    const [ consoleLog, setConsoleLog ] = useState();
    const [ logVotes, setLogVotes ] = useState();
    let history = useHistory();

    const Start = () => {
        setInGame(true);
        setMorningPhase(true);
        setNightPhase(false); 
        setDisabled(false);
        setUserKilled(false);
        setWitch(0);
        setAlive(true);
        setConsoleLog();
        setPlayersRemaining(6);
        setUserKills(0);
        setConsoleLog();
        setLogVotes();
        setWin(null);
    }

    const Select = (e) => {
        const selected = e.target.id;

        if (witch === 0) {
            let healer = Math.floor((Math.random() * playersRemaining));
            setWitch(healer);
        }

        if ( userKilled === false && playersRemaining >= 2 ) {
            if ( morningPhase === true ) { 
                let userVote = parseInt(selected, 10);
                let votes = []; 

                for (let i = 0; i <= playersRemaining - 1; i++) {
                    let random = Math.floor((Math.random() * playersRemaining));
                    votes.push(random);
                    console.log(`player ${i} has voted to hang player ${random}`);
                }
                votes.push(userVote);
                setLogVotes(votes);
                console.log(`You have voted to hang player ${userVote}`);                

                let highestReoccurence = 1;
                let counter = 0;
                let mostVotes = [];

                for (let i = 0; i <= votes.length; i++) {
                    for (let j = i; j <votes.length; j++) {
                        if (votes[i] === votes[j]) {
                            counter++;
                        } else if ( highestReoccurence < counter) {
                            highestReoccurence = counter
                            mostVotes = votes[i];
                        }
                    }
                    counter = 0;
                }
                    if (mostVotes === 0) {
                        console.log('You have been caught');
                        setConsoleLog('You have been caught')
                        setInGame(false);
                        setWin(false);
                        history.push('/postgame');
                    } 
                    else {
                        const hanged = document.getElementById(mostVotes); 
                        hanged.disabled = true;
                        hanged.value = false;
                        console.log(`Player ${mostVotes} has been voted to be hanged`);
                        setConsoleLog(`Player ${mostVotes} has been hanged`);
                    }

                highestReoccurence = 1
                counter = 0;
                mostVotes = [];
                votes = [];
                setPlayersRemaining(playersRemaining - 1)
                setMorningPhase(false);
                setNightPhase(true);
            }
            else if (nightPhase === true) {
                const eliminate = e.target.id
                const userEliminates = parseInt(eliminate, 10);
                // const theWitch = document.getElementById(witch)
                const playerEliminated = document.getElementById(userEliminates);
                const witchProtects = Math.floor((Math.random() * playersRemaining));

                // console.log(theWitch.value);
                // if (theWitch.value === false) {
                //     playerEliminated.disabled = true;
                //     playerEliminated.value = false;
                //     setPlayersRemaining(playersRemaining - 1);
                //     setUserKills(userKills + 1);
                //     console.log(`The werewolf eliminated player ${userEliminates}`);
                //     setConsoleLog(`The werewolf eliminated player ${userEliminates}`);
                // }
                // else if (theWitch.value === true) {
                    if (userEliminates === witchProtects) {
                        console.log(`Witch has healed player ${witchProtects} from a werewolf attack!`);
                        setConsoleLog(`Witch has healed player ${witchProtects} from a werewolf attack!`);
                    }
                    else if (userEliminates !== witchProtects) {
                        playerEliminated.disabled = true;
                        playerEliminated.value = false;
                        setPlayersRemaining(playersRemaining - 1);
                        setUserKills(userKills + 1);
                        console.log(`The werewolf eliminated player ${userEliminates}`);
                        setConsoleLog(`The werewolf eliminated player ${userEliminates}`);
                    } 
                    else {
                        alert('somethings wrong with your night-phase, if witch is alive statements');
                    }
                // }
                // else {
                //     alert('somethings wrong wtih if witch value === true/false');
                // }

                setLogVotes();
                setMorningPhase(true);
                setNightPhase(false);
            }
            else {
                alert('Something wrong with phases...');
            }
        }
        else if (playersRemaining === 1) {
            alert('Congratulations, you win!');
            setInGame(false);
            setWin(true);
            history.push('/postgame');
        }
        else {
            alert('Something is wrong with the game..');
        }
    }

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        })
        .then(response => {
            setUserData(response.data);
        });
    }, [])

    if (!token) {
        return <Redirect to='/' />
    }

    return(
        <>
            { !inGame?
                <button 
                    className='start-button'
                    onClick={Start}
                    >
                    Start
                </button>
            : 
            <>  
                <h2>
                    Players Remaining: {playersRemaining} &emsp; Kills: {userKills} &emsp; Witch: {witch}
                </h2>
                <h5 className="console"
                    >
                    {consoleLog}
                </h5>
                <div className='votes'>
                    <h3>
                        Votes:
                    </h3>
                { logVotes?   
                <>  
                    {logVotes.map(v => (
                        <p>
                            Player: {v}
                        </p>            
                    ))
                    }   
                </>
                :
                    <p>
                        No votes yet...
                    </p>
                }
                </div>

                { !morningPhase? 
                <>
                    <h1> 
                        NIGHT-PHASE
                    </h1>
                    <h2>
                        Who would you like to eliminate?
                    </h2>
                </>
                :
                <>
                    <h1>
                        MORNING-PHASE
                    </h1>
                    <h2>
                        Vote who you would like to "frame" as the "werewolf"
                    </h2>
                </>
                }
                <button className='player'
                    id={0}
                    value={userKilled}
                    disabled={disabled}
                    > 
                    {userData.username}
                </button>
                <button 
                    className='player'
                    id={1}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU1
                </button>
                <button 
                    className='player'
                    id={2}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU2
                </button>
                <button 
                    className='player'
                    id={3}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU3
                </button>
                <button 
                    className='player'
                    id={4}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU4
                </button> 
                <button 
                    className='player'
                    id={5}
                    disabled={disabled}
                    onClick={Select}
                    value={alive}
                    >
                    Morning-CPU5
                </button>
                <button 
                className='player'
                id={6}
                disabled={disabled}
                onClick={Select}
                value={alive}
                >
                Morning-CPU6
                </button>
            </>     
            }


        </>
    );
}


export default Play;
