import { useEffect, useState } from "react";
import axios from "axios";

const Play = ({ token }) => {
    const [ userData, setUserData ] = useState();
    const [ inGame, setInGame ] = useState(false);
    const [ nightPhase, setNightPhase ] = useState(false);
    const [ morningPhase, setMorningPhase ] = useState(false);



    const Start = () => {
        setInGame(true);
        setMorningPhase(true);
        setNightPhase(false);
    }

    useEffect(() => {
        axios.post('http://localhost:5000/accounts/me', {
            token: token,
        })
        .then(response => {
            console.log(response.data);
            setUserData(response.data);
        });
    }, [])

    return(
        <>
            { !inGame &&
                    <button 
                        className='start-button'
                        onClick={Start}>
                            Start
                    </button>
            }
            { !nightPhase &&
            <>
                    <button 
                        className='morning-user'>
                            Morning Werewolf
                    </button>
                    <button 
                        className='morning-cpu'>
                            Morning-CPU1
                    </button>
                    <button 
                        className='morning-cpu'>
                            Morning-CPU2
                    </button>
                    <button 
                        className='morning-cpu'>
                            Morning-CPU3
                    </button>
                    <button 
                        className='morning-cpu'>
                            Morning-CPU4
                    </button> 
                    <button 
                        className='morning-cpu'>
                            Morning-CPU5
                    </button>
            </>
            }
            { !morningPhase &&
            <>
                    <button 
                        className='night-user'>
                            Night Werewolf
                    </button>
                    <button 
                        className='night-cpu'>
                            Night-CPU1
                    </button>
                    <button 
                        className='night-cpu'>
                            Night-CPU2
                    </button>
                    <button 
                        className='night-cpu'>
                            Night-CPU3
                    </button>
                    <button 
                        className='night-cpu'>
                            Night-CPU4
                    </button> 
                    <button 
                        className='night-cpu'>
                            Night-CPU5
                    </button>
            </>
            }
        </>
    );
}

export default Play;
